import React, { useState, useEffect } from 'react'
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import InputAdornment from "@material-ui/core/InputAdornment";
import Icon from "@material-ui/core/Icon";
// @material-ui/icons
import Email from "@material-ui/icons/Email";
import People from "@material-ui/icons/People";
// core components
import Alert from '@mui/material/Alert';
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Button from "components/CustomButtons/Button.js";
import Card from "components/Card/Card.js";
import CardBody from "components/Card/CardBody.js";
import CardHeader from "components/Card/CardHeader.js";
import CardFooter from "components/Card/CardFooter.js";
import CustomInput from "components/CustomInput/CustomInput.js";

import styles from "assets/jss/material-kit-react/views/loginPage.js";

import image from "assets/img/bg7.jpg";

const useStyles = makeStyles(styles);

export default function LoginPage(props) {

    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [valid, setValid] = useState('')


    const submitData = (e) => {
        e.preventDefault();
        if (username !== '' && password !== '') {
            setValid("true")
            //    api.post('login', {username, password}).then(result => {
            //         window.alert("Login");
            //     }).catch(err => {
            //       console.log(err)
            //       window.alert(`Invalid Credentials ${err}`)
            //     })

            if (username == 'admin' && password == 'admin') {
                setValid("false")
                window.alert('Welcome to Admin Portal')
            } else {
                setValid("invalid")
                window.alert('Invalid Credentials')
            }

        } else {
            window.alert('Please fill all the fields')
            setValid("incomplete")
        alert()

        }
        alert()

    }
    const alert = () => {
        if (valid != "") {
            if (valid == "true") {
                return (
                    <Alert severity="success">This is a success alert — check it out!</Alert>
                )
            }
              else if (valid == "false") {

                return (<Alert severity="error">This is an error alert — check it out!</Alert>)
            }
         else {
            return (<Alert severity="warning">This is a warning alert — check it out!</Alert>)
        }
    }
        else {
        return (
            <>
            </>)
    }
}

const [cardAnimaton, setCardAnimation] = React.useState("cardHidden");
setTimeout(function () {
    setCardAnimation("");
}, 700);
const classes = useStyles();
const { ...rest } = props;
return (
    <div>
        {alert}

        <div
            className={classes.pageHeader}
            style={{
                backgroundImage: "url(" + image + ")",
                backgroundSize: "cover",
                backgroundPosition: "top center",
            }}
        >


            <div className={classes.container}>
                <GridContainer justify="center">
                    <GridItem xs={12} sm={12} md={4}>
                        <Card className={classes[cardAnimaton]}>
                            <form className={classes.form}>
                                <CardHeader color="success" className={classes.cardHeader}>
                                    <h3>ERFA Admin Portal</h3>
                                </CardHeader>
                                <p className={classes.divider}>Enter your Credentials</p>
                                <CardBody>
                                    <CustomInput
                                        labelText="Username"
                                        id="username"
                                        formControlProps={{
                                            fullWidth: true,
                                        }}
                                        inputProps={{
                                            onChange: (event) => setUsername(event.target.value),
                                            type: "text",
                                            endAdornment: (
                                                <InputAdornment position="end">
                                                    <People className={classes.inputIconsColor} />

                                                </InputAdornment>
                                            ),
                                        }}

                                    />
                                    <CustomInput
                                        labelText="Password"
                                        id="pass"
                                        formControlProps={{
                                            fullWidth: true,
                                        }}
                                        inputProps={{
                                            onChange: (event) => setPassword(event.target.value),
                                            type: "password",
                                            endAdornment: (
                                                <InputAdornment position="end">
                                                    <Icon className={classes.inputIconsColor}>
                                                        lock_outline
                                                    </Icon>
                                                </InputAdornment>
                                            ),
                                            autoComplete: "off",
                                        }}
                                    />
                                </CardBody>
                                <CardFooter className={classes.cardFooter}>
                                    <Button simple color="primary" size="lg" onClick={submitData}>
                                        Login
                                    </Button >
                                </CardFooter>
                            </form>
                        </Card>
                    </GridItem>
                </GridContainer>
            </div>

        </div>
        <prev >{JSON.stringify(username, null, 2)}</prev>
        <prev>{JSON.stringify(password, null, 2)}</prev>
        <prev>{JSON.stringify(valid, null, 2)}</prev>

    </div>
);
}
